import React from "react"
import { Card, CardImg, CardText, CardBody,
  CardTitle, CardSubtitle, Button, ListGroup, ListGroupItem, ListGroupItemHeading, ListGroupItemText } from 'reactstrap'



class AllApartments extends React.Component {
  constructor () {
    super ()
    this.state = {
      allApts:[]
    }
    this.getApartments()
  }

  componentWillMount(){
    this.getApartments()
  }

  getApartments = () => {
    fetch("/apartments")
    .then((response)=>{
      if(response.status ===200){
        return(response.json())
      }
    })
    .then((aptArray) => {
      this.setState({allApts: aptArray})
    })
  }


  render () {

    return (
      <>


            { this.state.allApts.map((apt, index) => {
                return(
                  <Card key= { index }>
                    <CardImg width="318px" src={apt.picture_url} alt="Card image cap" />
                    <CardBody>
                      <CardTitle>{ apt.city }, { apt.state }</CardTitle>
                      <CardSubtitle>Card subtitle</CardSubtitle>
                      <CardText>{ apt.description }</CardText>
                      <Button>View More Info</Button>
                    </CardBody>
                  </Card>
                )
            })}


      </>
    );
  }
}

export default AllApartments
