import React from "react"
import { Nav, NavItem, NavLink, ListGroup, ListGroupItem, ListGroupItemHeading, ListGroupItemText } from 'reactstrap'
import NewApartment from "./NewApartment"



class AllApartments extends React.Component {
  constructor () {
    super ()
    this.state = {
      allApts:[]
    }
    this.getApartments()
  }

  componentWillMount(){
    this.getApartments()
  }

  getApartments = () => {
    fetch("http://localhost:3000/apartments")
    .then((response)=>{
      if(response.status ===200){
        return(response.json())
      }
    })
    .then((aptArray) => {
      this.setState({allApts: aptArray})
    })
  }


  render () {

    return (
      <>

        <h2>ALL APARTMENTS</h2>
        <ListGroup >
            { this.state.allApts.map((apt, index) => {
                return(
                    <ListGroupItem key= { index }>
                        <ListGroupItemHeading> Address: { apt.street_num }, { apt.city }, { apt.state } { apt.zip } </ListGroupItemHeading>
                        <ListGroupItemText>Description: { apt.description }</ListGroupItemText>
                        <img src={apt.picture_url}/>
                    </ListGroupItem> 
                )
            })}
          </ListGroup>

      </>
    );
  }
}

export default AllApartments
